#include <stdio.h>

int 
main()
{
	int a, db=0;
	printf("Adj meg egy decimalis szamot!\n");
	scanf("%d", &a);
	printf("Atvaltva unarisba:\n");
	for (int i = 0; i < a; i++)
	{
		printf("|");
		db++;
		if (db % 5 == 0) 
                {
                printf(" ");
                }
	}
	printf("\n");
	return 0;
}

#include<stdio.h>

int main()
{
    int y;
    for (int a = 0; a < 10; a++) 
    {   
        y=a*2;
        printf("%d \n", y);
    }

    return 0;
}

